    def checkAccount(self):
        if len(self.usernameinput.text()) != 0 and len(self.passwordinput.text()) != 0:
   
                import mysql.connector
                db = mysql.connector.connect(
                    host = 'localhost',
                    user = 'root',
                    password = 'root',
                    database = 'emp_management_db'
                )
                cursor = db.cursor()
                cursor.execute(f'select * from emp_account where EmpUsername = "{self.usernameinput.text()}"')
                accounts = cursor.fetchall()

                validation = 0

                for account in accounts:
                    self.employeeName = account[0]
