    def acceptAttendance(self):
        from datetime import datetime
        from PyQt6.QtWidgets import QMessageBox
        import mysql.connector
        
        db = mysql.connector.connect(
            host = 'localhost',
            user = 'root',
            password = 'root',
            database = 'emp_management_db'
        )
        cursor = db.cursor()

        dt = datetime.now()
        DateTime = dt.strftime('%B %d, %Y - %I:%M%p ')
        cursor.execute(f'insert into emp_attendance values("{self.employeeName}","{DateTime}")')
        db.commit()
        
        self.log = QMessageBox()
        self.log.setWindowTitle('Employee Management System')
        self.log.setWindowIcon(QtGui.QIcon('profile.png'))
        self.log.setIcon(QMessageBox.Icon.Information)
        self.log.setText('Welcome, you are now logged in.')
        self.log.show()

